// num1 + num2 = sum
function sum(maxNumb) {
    var currentValue = 1;
    var prevValue = 0;
    var sum = 0;
    var temp;
    //4
    if (maxNumb <= 0)
        return 0;
    if (maxNumb == 1)
        return 1;
    //5
    sum = 1;
    for (var i = 2; i < maxNumb; i++) {
        temp = currentValue;
        currentValue = prevValue + currentValue;
        prevValue = temp;
        sum += currentValue;
    }
    return sum;
}
console.log(sum(10));
