// num1 + num2 = sum

function sum(maxNumb: number): number {

    let currentValue = 1;
    let prevValue = 0;
    let sum = 0;
    let temp;

    if (maxNumb <= 0)
        return 0;

    if (maxNumb == 1)
        return 1;

    sum = 1;
    for (let i = 2; i < maxNumb; i++) {
        temp = currentValue;
        currentValue = prevValue + currentValue;
        prevValue = temp;

        sum += currentValue;
    }
    return sum;
}

console.log(sum(10));